int main(int argc, char** argv) {
    return munit_suite_main(&suite, NULL, argc, argv);
}

void* test_setup(const MunitParameter params[], void* user_data) {
    test_jq_t* test_jq = (test_jq_t*) malloc(sizeof(test_jq_t));

    ipc_jobqueue_t* q = ipc_jobqueue_new(new_init_proc());
    
    test_jq->q = q;
    test_jq->qimpl = (pri_jobqueue_t*) q->addr;
    
    // assert valid queue
    assert_queue_initialised(test_jq->qimpl);

    test_jq->dequeue = (job_t* (*)(void*, job_t*)) ipc_jobqueue_dequeue;
    test_jq->enqueue = (void (*)(void*, job_t*))  ipc_jobqueue_enqueue;
    test_jq->is_empty = (bool (*)(void*)) ipc_jobqueue_is_empty;
    test_jq->is_full = (bool (*)(void*)) ipc_jobqueue_is_full;
    test_jq->peek = (job_t* (*)(void*, job_t*)) ipc_jobqueue_peek;
    test_jq->size = (int (*)(void*)) ipc_jobqueue_size;
    test_jq->space = (int (*)(void*)) ipc_jobqueue_space;

    return test_jq;
}

void test_tear_down(void* fixture) {
    test_jq_t* test_jq = (test_jq_t*) fixture;
    ipc_jobqueue_t* q = (ipc_jobqueue_t*) test_jq->q;
   
    proc_t* p = q->proc;
    ipc_delete(q);
    proc_delete(p);
    free(test_jq);
}

