MunitResult test_ipc_jobqueue_delete(const MunitParameter params[], 
    void* fixture) {
    proc_t* proc = new_init_proc();
    ipc_jobqueue_t* q = ipc_jobqueue_new(proc);
    
    char* qname = q->name;
    
    /* the following should just not cause errors */
    ipc_jobqueue_delete(q);
    
    /* make sure that unlink shared memory in case ipc_jobqueue_delete 
     * doesn't 
     */
    shm_unlink(qname);
    
    free(proc);

    /* check NULL OK */    
    ipc_jobqueue_delete(NULL);

    return MUNIT_OK;
}

